<?php/** * Created by PhpStorm. * User: Jawhar * Date: 24/03/2019 * Time: 20:32 */include_once('common.php');class DB{    static private $connexion;    static public function connect()    {       if (empty(static::$connexion)) {           static::CreateDBConnection();       }       return static::$connexion;    }    /** Database Connection     * Try to connect to DB and returns PDO connexion instance , or dies with Error     *     * return : $coonnexion     */    static private function CreateDBConnection()    {        try {            $connexion = new PDO("mysql:dbname=".DB_Config['DATABASE'].                                     ";host=".DB_Config['HOST'].";charset=utf8",                                    DB_Config['USERNAME'],                                    DB_Config['PASSWORD']);            if ($connexion) {                static::$connexion = $connexion;            } else {                E("Impossible de se connecter à la base de données");            }        } catch (PDOException $e) {            E("Impossible de se connecter à la base de données");        }    }}